(function(){tinymce.PluginManager.requireLangPack('umbraco');tinymce.create('tinymce.plugins.umbracoMacro',{init:function(ed,url){ed.addCommand('mceUmbracomacro',function(){ed.windowManager.open({file:tinyMCE.activeEditor.getParam('umbraco_path')+'/plugins/tinymce3/insertMacro.aspx?editor=true&umbPageId='+tinyMCE.activeEditor.getParam('umbPageId')+'&umbVersionId='+tinyMCE.activeEditor.getParam('umbVersionId'),width:480+parseInt(ed.getLang('umbracoMacro.delta_width',0)),height:570+parseInt(ed.getLang('umbracoMacro.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('umbracoMacro',{title:'umbracoMacro.desc',cmd:'mceUmbracomacro',image:url+'/images/macro.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('embedcode',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Umbraco Macro Insertion Plugin',author:'Umbraco',authorurl:'http://umbraco.org',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/embedcode',version:"1.0"}}});tinymce.PluginManager.add('umbracomacro',tinymce.plugins.umbracoMacro)})();